import React, { Component } from 'react';
import {View,  TextInput, StyleSheet, Text} from 'react-native-web';
import Princiapl from './screen/Principal'
import Descargas from './screen/Descargas'
import pluma from './images/Pluma.png'
import Lottie from './screen/LottieLoop'

class App extends Component {

constructor(props){

  super(props);
  this.state={
      mensaje:'Hola Mundo',
      variable : '',
      pass: 'anuario2020',
      descarga: 'gdsduoscj2020'
  }


}

funRevisarLogin(){


  if(this.state.variable===this.state.pass){
    return(
      <Princiapl />
    )
  }
  else if(this.state.variable===this.state.descarga){
    return(
      <Descargas />
    )
  }
  else {
    return(
      <View style={styles.title}>
      <TextInput style = {styles.input} 
              autoCapitalize="none" 

              onChangeText={(variable) => this.setState({ variable })}
              autoCorrect={false} 
              keyboardType='email-address' 
              returnKeyType="next" 
              placeholder='Clave Acceso' 
              placeholderTextColor='#FFF'/>

      <img src={pluma} alt="Pluma" ></img>
   </View>
    )
  }


}



  render() {
    return (




        <View>

           {this.funRevisarLogin()}
        </View>  

    );
  }
}








export default (App);



const styles = StyleSheet.create({
  input:{
    height: 40,
    width: 150,
    backgroundColor: '#ddd',
    borderRadius: 30,
    marginBottom: 10,
    padding: 10,
    color: '#666',
    alignItems: 'center'
  },

  title: {


    alignItems:  'center',
    justifyContent: 'center',

    margin: 10,
    padding: 10,


  },
});
